mapper.select:
select

from



    and id = #{id}


    and year_id = #{yearId}


    and semester_id = #{semesterId}


    and grade_id = #{gradeId}


    and class_id = #{classId}


    and class_type = #{classType}


    and student_id = #{studentId}


    and teacher_id = #{teacherId}


    and sub_language = #{subLanguage}


    and sub_mathematics = #{subMathematics}


    and sub_english = #{subEnglish}


    and sub_history = #{subHistory}


    and sub_geography = #{subGeography}


    and sub_biological = #{subBiological}


    and sub_chemistry = #{subChemistry}


    and sub_physical = #{subPhysical}


    and sub_political = #{subPolitical}


    and sub_computer = #{subComputer}


    and sub_sports = #{subSports}


    and sub_art = #{subArt}


    and sub_music = #{subMusic}


    and insert_time = #{insertTime}


    and update_time = #{updateTime}


    and student_id in

      #{studentId}

SELECT GRA.GRADE_ID, GRA.GRADE_NAME, CLA.CLASS_ID, CLA.CLASS_NAME, CLA.CLASS_TYPE, YEA.YEAR_ID,YEA.YEAR_NAME,SEM.SEMESTER_ID,SEM.SEMESTER_NAME, AVG(S.SUB_LANGUAGE) AVG_LANGUAGE, AVG(S.SUB_MATHEMATICS) AVG_MATHEMATICS, AVG(S.SUB_ENGLISH) AVG_ENGLISH, AVG(S.SUB_HISTORY ) AVG_HISTORY, AVG(S.SUB_GEOGRAPHY ) AVG_GEOGRAPHY, AVG(S.SUB_BIOLOGICAL ) AVG_BIOLOGICAL, AVG(S.SUB_CHEMISTRY ) AVG_CHEMISTRY, AVG(S.SUB_PHYSICAL ) AVG_PHYSICAL, AVG(S.SUB_POLITICAL ) AVG_POLITICAL, AVG(S.SUB_COMPUTER ) AVG_COMPUTER, AVG(S.SUB_SPORTS ) AVG_SPORTS, AVG(S.SUB_ART ) AVG_ART, AVG(S.SUB_MUSIC ) AVG_MUSIC, AVG(S.SUB_SCORE_SUM ) AVG_SCORE_SUM FROM achievement.SUBJECT_SCORE_INFO S, achievement.CLASS_INFO CLA, achievement.GRADE_INFO GRA, achievement.YEAR_INFO YEA,SEMESTER_INFO SEM
S.CLASS_ID = CLA.CLASS_ID AND CLA.GRADE_ID = GRA.GRADE_ID AND S.SEMESTER_ID=SEM.SEMESTER_ID AND YEA.YEAR_ID=SEM.YEAR_ID
AND YEA.YEAR_ID=#{yearId}
AND S.SEMESTER_ID=#{semesterId}
AND CLA.GRADE_ID=#{gradeId}
AND CLA.CLASS_TYPE=#{classType}
GROUP BY S.SEMESTER_ID, CLA.CLASS_ID, GRA.GRADE_ID,CLA.CLASS_TYPE,SEM.SEMESTER_ID, YEA.YEAR_ID ORDER BY AVG(S.SUB_SCORE_SUM) DESC,S.CLASS_ID
